/*2. Generar las sentencias SQL para la creación de la base de datos, las tablas correspondientes al diagrama anterior y demás objetos requeridos para satisfacer
los puntos 4, 5 y 6 detallados a continuación.*/IF NOT EXISTS (SELECT name FROM sys.databases WHERE name = 'Gestion_Arbolado')	CREATE DATABASE Gestion_ArboladoUSE Gestion_ArboladoIF NOT EXISTS (SELECT 1 FROM sys.tables WHERE name = 'Arboles')CREATE TABLE Arboles	(ID_Arbol INT IDENTITY(1,1) PRIMARY KEY	,Cod_Arbol VARCHAR(20) UNIQUE NOT NULL	,ID_Especie INT NOT NULL	,ID_Ubicacion INT	,Coordenadas VARCHAR(50)	,Fecha_Plantacion DATE)IF NOT EXISTS (SELECT 1 FROM sys.tables WHERE name = 'Tareas')CREATE TABLE Tareas	(ID_Tarea INT IDENTITY(1,1) PRIMARY KEY	,ID_Tipo_Tarea INT	,ID_Cuadrilla INT	,Fecha_Planificada DATE	,Comentario VARCHAR(200)	,Fecha_Realizada DATE)IF NOT EXISTS (SELECT 1 FROM sys.tables WHERE name = 'Tipos_Tareas')CREATE TABLE Tipos_Tareas	(ID_Tipo_Tarea INT IDENTITY(1,1) PRIMARY KEY	,Descripcion VARCHAR(50))IF NOT EXISTS (SELECT 1 FROM sys.tables WHERE name = 'Tareas_Arboles')CREATE TABLE Tareas_Arboles	(ID_Tareas_Arboles INT IDENTITY(1,1) PRIMARY KEY	,ID_Tarea INT	,ID_Arbol INT)